% this function is used inside of useMLfunction_newData. You shouldn't need to
% change anything in here, unless you want to change the fundementals

function [pctCorrectTest,confusionMatrix,tree] = runMLNew(method,model,splitNumber,filepath)


%% construct filename from function input (if you want to include a new method type, this is the place)
switch method 
    case 'ftir'
        testFilename = ['ftir_test_',splitNumber,'.csv']
        trainFilename = ['ftir_train_',splitNumber,'.csv']
    case 'xrfSpec'  
        testFilename = ['xrfSpec_test_',splitNumber,'.csv']
        trainFilename = ['xrfSpec_train_',splitNumber,'.csv']
    case 'xrfConc'  
        testFilename = ['xrfConc_test_',splitNumber,'.csv']
        trainFilename = ['xrfConc_train_',splitNumber,'.csv']
    case 'libs'
        testFilename = ['libs_test_',splitNumber,'.csv']
        trainFilename = ['libs_train_',splitNumber,'.csv']
    case 'qcl'
        testFilename = ['qcl_test_',splitNumber,'.csv']
        trainFilename = ['qcl_train_',splitNumber,'.csv']
    case 'refIR'
        testFilename = ['refIR_test_',splitNumber,'.csv']
        trainFilename = ['refIR_train_',splitNumber,'.csv']
    otherwise 
        warning('Unexpected method input! Check spelling please.')
end

 
%% Import data 
testData = csvread([filepath,testFilename],1,3); % for some reason this is the only part of matlab that's ever 0 referenced!
trainData = csvread([filepath,trainFilename],1,3);

